import React from 'react';
import { Link } from 'react-router-dom';
import apparelImage from '../assets/products/Apparel/hxh-tee.jpg';
import accessoriesImage from '../assets/products/Keychains/goku-keychain.jpg';
import figuresImage from '../assets/products/Figures/luffy-funko.jpg';

const Home = () => {
    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
            <h1 className="text-4xl font-bold text-center mb-4">Welcome to This N That Mix Up Shop!</h1>
            <p className="text-lg text-center mb-8">Your one-stop shop for unique apparel, accessories, and collectibles.</p>

            {/* Section Links */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 w-full px-6">
                {/* Apparel Section */}
                <Link to="/apparel" className="relative group">
                    <img
                        src={apparelImage}
                        alt="Apparel"
                        className="w-full h-64 object-cover rounded-lg shadow-lg"
                    />
                    <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                        <span className="text-white text-2xl font-bold">Apparel</span>
                    </div>
                </Link>

                {/* Accessories Section */}
                <Link to="/accessories" className="relative group">
                    <img
                        src={accessoriesImage}
                        alt="Accessories"
                        className="w-full h-64 object-cover rounded-lg shadow-lg"
                    />
                    <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                        <span className="text-white text-2xl font-bold">Accessories</span>
                    </div>
                </Link>

                {/* Figures Section */}
                <Link to="/figures" className="relative group">
                    <img
                        src={figuresImage}
                        alt="Figures"
                        className="w-full h-64 object-cover rounded-lg shadow-lg"
                    />
                    <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                        <span className="text-white text-2xl font-bold">Figures</span>
                    </div>
                </Link>
            </div>
        </div>
    );
};

export default Home;
