import React from 'react';
import { Link } from 'react-router-dom';
import apparelImage from '../assets/products/Apparel/hxh-tee.jpg';
import accessoriesImage from '../assets/products/Keychains/goku-keychain.jpg';
import figuresImage from '../assets/products/Figures/luffy-funko.jpg';
import apparelImage1 from '../assets/products/Apparel/luffy-tee.jpg'; // Replace with actual file name
import apparelImage2 from '../assets/products/Apparel/aot-logo-tee.jpg'; // Replace with actual file name
import dryfitImage from '../assets/products/DryFit/momo-dryfit.jpg'; // Replace with actual file name

const Home = () => {
    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
            <h1 className="text-4xl font-bold text-center mb-4">Welcome to This N That Mix Up Shop!</h1>
            <p className="text-lg text-center mb-8">Your one-stop shop for unique apparel, accessories, and collectibles.</p>

            {/* Section Links */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 w-full max-w-4xl px-4">
                <Link to="/apparel" className="text-center text-lg font-bold hover:underline">
                    Apparel
                </Link>
                <Link to="/accessories" className="text-center text-lg font-bold hover:underline">
                    Accessories
                </Link>
                <Link to="/figures" className="text-center text-lg font-bold hover:underline">
                    Figures
                </Link>
            </div>

            {/* Images Section */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 w-full max-w-4xl px-4 mt-4">
                {/* Apparel Images */}
                <div className="relative group">
                    <img
                        src={apparelImage1}
                        alt="Apparel Product 1"
                        className="w-full h-64 object-cover rounded-lg shadow-lg"
                    />
                </div>
                <div className="relative group">
                    <img
                        src={apparelImage2}
                        alt="Apparel Product 2"
                        className="w-full h-64 object-cover rounded-lg shadow-lg"
                    />
                </div>

                {/* Dryfit Image */}
                <div className="relative group">
                    <img
                        src={dryfitImage}
                        alt="Dryfit Product"
                        className="w-full h-64 object-cover rounded-lg shadow-lg"
                    />
                </div>
            </div>
        </div>
    );
};

export default Home;
