import React from 'react';

const AboutUs = () => {
    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold mb-4">About Us</h1>
            <p className="mb-4">
                Welcome to This N That Mix Up Shop! We are dedicated to providing a unique shopping experience with a diverse range of products, from stylish apparel to eye-catching accessories and collectible figures.
            </p>
            <p className="mb-4">
                Our mission is to bring together a mix of trendy and classic items that cater to all tastes and preferences. Whether you're looking for the latest fashion trends or a special gift for a loved one, we have something for everyone.
            </p>
            <p>
                Thank you for visiting our shop, and we hope you enjoy your experience with us!
            </p>
        </div>
    );
};

export default AboutUs;
