import React from 'react';
import { Link } from 'react-router-dom';
import apparelImage from '../assets/products/Apparel/hxh-tee.jpg';
import accessoriesImage from '../assets/products/Keychains/goku-keychain.jpg';
import figuresImage from '../assets/products/Figures/luffy-funko.jpg';
import apparelImage1 from '../assets/products/Apparel/luffy-tee.jpg';
import apparelImage2 from '../assets/products/Apparel/aot-logo-tee.jpg';
import dryfitImage from '../assets/products/DryFit/momo-dryfit.jpg';

const Home = () => {
    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">

            {/* Welcome Title */}
            <h1 className="text-4xl font-bold text-center mb-2">Welcome to This N That Mix Up Shop!</h1>

            {/* Section Links */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 w-full max-w-4xl px-4">
                {/* Apparel Section */}
                <Link to="/apparel" className="relative group">
                    <div className="relative w-40 h-40 mx-auto">
                        <img
                            src={apparelImage}
                            alt="Apparel"
                            className="w-full h-full object-cover rounded-lg shadow-lg"
                        />
                        <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                            <span className="text-white text-lg font-bold">Apparel</span>
                        </div>
                    </div>
                </Link>

                {/* Accessories Section */}
                <Link to="/accessories" className="relative group">
                    <div className="relative w-40 h-40 mx-auto">
                        <img
                            src={accessoriesImage}
                            alt="Accessories"
                            className="w-full h-full object-cover rounded-lg shadow-lg"
                        />
                        <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                            <span className="text-white text-lg font-bold">Accessories</span>
                        </div>
                    </div>
                </Link>

                {/* Figures Section */}
                <Link to="/figures" className="relative group">
                    <div className="relative w-40 h-40 mx-auto">
                        <img
                            src={figuresImage}
                            alt="Figures"
                            className="w-full h-full object-cover rounded-lg shadow-lg"
                        />
                        <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                            <span className="text-white text-lg font-bold">Figures</span>
                        </div>
                    </div>
                </Link>
            </div>

            {/* Apparel Section */}
            <div className="w-full max-w-4xl px-4 mt-8">
                <h2 className="text-2xl font-bold mb-4 text-center">Apparel</h2> {/* Centered Title */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {/* Apparel Product 1 */}
                    <div className="relative group">
                        <img
                            src={apparelImage1}
                            alt="Apparel Product 1"
                            className="w-full h-64 object-cover rounded-lg shadow-lg"
                        />
                    </div>

                    {/* Apparel Product 2 */}
                    <div className="relative group">
                        <img
                            src={apparelImage2}
                            alt="Apparel Product 2"
                            className="w-full h-64 object-cover rounded-lg shadow-lg"
                        />
                    </div>

                    {/* Dryfit Product */}
                    <div className="relative group">
                        <img
                            src={dryfitImage}
                            alt="Dryfit Product"
                            className="w-full h-64 object-cover rounded-lg shadow-lg"
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;
